import React, {useEffect, useState} from 'react';
import axios from 'axios';

export default function Careers(){
  const [careers, setCareers] = useState([]);

  useEffect(()=> {
    axios.get('http://localhost:5000/api/careers').then(res => setCareers(res.data)).catch(()=>{});
  }, []);

  return (
    <div className="max-w-6xl mx-auto p-8">
      <h2 className="text-2xl font-semibold mb-6">Explore Careers</h2>
      <div className="grid md:grid-cols-3 gap-6">
        {careers.length===0 && <p className="text-gray-500">No careers available. (You can add via backend POST /api/careers)</p>}
        {careers.map(c=>(
          <div key={c._id} className="bg-white rounded shadow p-4 hover:shadow-lg transition">
            <h3 className="font-bold">{c.title}</h3>
            <p className="text-sm text-gray-600 my-2">{c.description}</p>
            <p className="text-xs"><strong>Skills:</strong> {c.skills?.join(', ')}</p>
            {c.resources?.length>0 && (
              <div className="mt-3">
                <strong className="text-sm">Resources</strong>
                <ul className="text-xs list-disc ml-4">
                  {c.resources.map((r,i)=>(<li key={i}><a href={r.link} target="_blank" rel="noreferrer" className="text-indigo-600">{r.title}</a></li>))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
